import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { Grow, Container, Grid, Typography, Fab, Slide } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add'
import EditIcon from '@material-ui/icons/Edit'
import EmployeeCard from './employeeCard/EmployeeCard'
import NewEmployeeForm from './employeeForm/EmployeeForm'
import useStyles from './styles'

const EmployeesView = () => {
    const classes = useStyles()
    const employees = useSelector((state) => state.employees)
    const [showEmployeeForm, setShowEmployeeForm] = useState(false)
    const [editEmployees, setEditEmployees] = useState(false)

    const handleAddButton = () => {
        setShowEmployeeForm(!showEmployeeForm)
    }

    const handleEditButton = () => {
        setEditEmployees((prev) => !prev)
        return (
            <Grid container className={classes.grid} spacing={1}>
                    {employees.map((employee) => (
                        <Grid key={employee._id} item>
                            <EmployeeCard employee={employee} editMode={editEmployees}/>
                        </Grid>
                    ))}
            </Grid>
        )
    }

    return (
        <Grid className={classes.center}>


            {/* <NewEmployeeForm /> */}

            <Grow in>
                <Grid container className={classes.grid} spacing={1}>
                    {employees.map((employee) => (
                        <Grid key={employee._id} item>
                            <EmployeeCard employee={employee} editMode={editEmployees}/>
                        </Grid>
                    ))}
                </Grid>
            </Grow>
            <Grid className={classes.buttonTray}>
                <Fab className={classes.button} color="primary" aria-label="addButton" onClick={handleAddButton}>
                    <AddIcon />
                </Fab>
                <Fab className={classes.button} color="primary" aria-label="editButton" onClick={handleEditButton}>
                    <EditIcon />
                </Fab>
            </Grid>
            <Slide direction="up" in={showEmployeeForm} mountOnEnter unmountOnExit>
                <Grid container className={classes.employeeFormContainer}>
                    <NewEmployeeForm />
                </Grid>
            </Slide>
        </Grid>
    )
}

export default EmployeesView
