import { useSelector } from 'react-redux'
import { useState } from 'react'
import { Menu, MenuItem, IconButton, Fade } from '@material-ui/core'
import MoreVertIcon from '@material-ui/icons/MoreVert'


const DropDownMenu = ({setSelectedEmployee}) => {
    const employees = useSelector((state) => state.employees)
    const [option, setOption] = useState(employees[0])
    const ITEM_HEIGHT = 48
    const [anchorEl, setAnchorEl] = useState(null)
    const open = Boolean(anchorEl)

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget)
    }

    const handleClose = (event) => {
        const name = event.target.innerText
        const  myValue  = event.currentTarget.dataset
        // if(name) findByName(name)
        // console.log(name)
        console.log(myValue.key)
        setAnchorEl(null);
    }

    const setEmployee = (event) => {
        const employee = event.target.value
        console.log(employee)
    }

    // const findByName = (name) => {
    //     //let employee = []
    //     const firstName = name.split(' ')[0]
    //     const lastName = name.split(' ')[1]


    //     const employee = employees.forEach((emp) => {
    //         const fullName = emp.firstName + " " + emp.lastName
    //         if(name === fullName) {
    //             return emp
    //         }
    //     })
    //     console.log(employee)
    // }


    return (
        <div>
            <IconButton
                aria-label="more"
                aria-controls="long-menu"
                aria-haspopup="true"
                onClick={handleClick}
            >
                <MoreVertIcon />
            </IconButton>
            <Menu
                id="long-menu"
                anchorEl={anchorEl}
                keepMounted
                value={option}
                open={open}
                onClose={handleClose}

                PaperProps={{
                    style: {
                        maxHeight: ITEM_HEIGHT * 4.5,
                        width: '40ch',
                    },
                }}
            >
            {employees.map((employee) => (
                <MenuItem key={employee} data-my-value={employee} onClick={handleClose} >
                    {employee.firstName} {employee.lastName}
                </MenuItem>
            ))}
            </Menu>
        </div>
    )
}

export default DropDownMenu
