import { makeStyles } from '@material-ui/core/styles'
import { 
    ViewState, 
    EditingState, 
    IntegratedEditing 
} from '@devexpress/dx-react-scheduler'
import { 
    Scheduler, 
    DayView, 
    WeekView,
    Appointments, 
    AppointmentForm,
    ConfirmationDialog,
    AppointmentTooltip,
    Toolbar,
    ViewSwitcher,
    TodayButton,
    DateNavigator,
} from '@devexpress/dx-react-scheduler-material-ui'
import Card from '@material-ui/core/Card'
import { appointments } from '../../testData/testTimes'
import shifts from '../../testData/weekShifts'
import { useSelector } from 'react-redux'

const Calendar = ({employeesSchedules}) => {
    const employees = useSelector((state) => state.employees).slice()
    const currentDate = new Date()

    const createSchedule = (employees) => {
        let scheduleWithNames = []
        employees.forEach((employee) => {
            //let name = employee.firstName + " " + employee.lastName
            employee.weekSchedule.forEach((shift) => {
                const titleWithName = employee.firstName + " " + employee.lastName + " - " + shift.title
                shift.title = titleWithName
                scheduleWithNames.push(shift)
            })
        })
        return scheduleWithNames
    }
    let schedulerData = employees ? createSchedule(employees) : null

    return (
        <Scheduler
            data={schedulerData}
            firstDayOfWeek={1}
        >
            <ViewState
                defaultCurrentDate={currentDate}
            />
            <DayView
                startDayHour={7}
                cellDuration={60}
                endDayHour={23}
            />
            <WeekView
                startDayHour={7}
                cellDuration={60}
                endDayHour={23}
            />
            <Toolbar />
            <ViewSwitcher />
            <Appointments />
            <AppointmentTooltip 
                showCloseButton
                showDeleteButton
            />
            <TodayButton />
            <DateNavigator />
        </Scheduler>
    )
}

export default Calendar
