import { makeStyles } from '@material-ui/core'
import abstractImage from '../../images/Abstract-Envelope.svg'
import calendarImage from '../../images/annual-calendar-page.png'


export default makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        width: '75vw',
        //maxHeight: '80vw',
        // display: 'flex',
        // flexDirection: 'column',
        // alignItems:'center',
        // justifyContent:'center',
        [theme.breakpoints.down('xs')]: {
            width: '100vw',

        }
    },
    homeDisplay: {
        //display: 'flex',
        flexDirecton: 'column',
        alignItems: 'center',
        justifyContent: 'center',
    },
    cardDisplay: {
        width: 150,
        height: 110,
        borderRadius: 10,
        zIndex: 0,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center',
        // background: 'linear-gradient(90deg, #00C9FF 0%, #92FE9D 100%)',
        background: theme.palette.primary.main,
        boxShadow: '0 5px 40px -12px rgba(0,0,0,0.8)',
        [theme.breakpoints.down('sm')]: {
            width: 120,
            height: 100,
        },
        [theme.breakpoints.down('xs')]: {
            width: 120,
            height: 100,
        },
        '& hover': {
            backgroundImage: theme.palette.secondary.main,
        }
    },
    titleCard: {
        width: 500,
        height: 500,
        // borderRadius: 10,
        boxShadow: '0 5px 40px -12px rgba(0,0,0,0.8)',
        display: 'relative',
        // direction: 'column',
        // alignItems: 'center',
        [theme.breakpoints.down('sm')]: {
            height: 400,
        },
        [theme.breakpoints.down('xs')]: {
            height: 300,
        },
    },
    title: {
        // position: 'absolute',
        fontSize: 50,
        fontWeight: 900,
        textTransform: 'uppercase',
        [theme.breakpoints.down('sm')]: {
            fontSize: 23,
        },
        [theme.breakpoints.down('sm')]: {
            fontSize: 22,
        },
    },
    logo: {
        width: 90,
        margin: 5,
    },
}))
