import { useState } from 'react'
import { makeStyles } from '@material-ui/core'
import ReactCardFlip from 'react-card-flip'
import Card from '@material-ui/core/Card'
import Container from '@material-ui/core/Container'
import EmployeesView from '../EmployeesPage'

const useStyles = makeStyles((theme) => ({
    card: {
        height: '200px',
        margin: '10px',
        width: '200px',
        borderRadius: '7px',
        boxShadow: '0 8px 40px -12px rgba(0,0,0,0.8)',
        transition: '0.1s',
        backgroundImage: 'radial-gradient(circle, rgba(3,38,0,1) 0%, rgba(103,135,20,1) 91%, rgba(226,253,45,1) 100%)',
        '&:hover': {
            height: '250px',
            width: '200px',
            marginTop:'-25px',
            marginBottom:'-25px',
            fontSize:'120%',
            backgroundImage: 'radial-gradient(circle, rgba(3,38,0,1) 80%, rgba(103,135,20,1) 91%, rgba(226,253,45,1) 100%)',
            boxShadow: '0 16px 70px -12.125px rgba(0,0,0,0.8)',
            ctaContainer: {
                display:'inline',
                marginTop: '3rem',
            },
            cardContent: {
                marginTop: '2rem',
            }
        }
    },
    cardCircle: {
        backgroundColor: '#3487f7',
        backgroundSize: 'cover',
        borderRadius: '50%',
        height: '400px',
        marginLeft: '-75px',
        marginTop: '-270px',
        position: 'absolute',
        width: '450px',
    },
    cardContent: {
        height: '200px',
        justifyContent: 'center',
        textAlign: 'center',
        margin:'auto',
        alignContent: 'center',
    }
}))

const EmployeeCard = ({employee}) => {
    const classes = useStyles()

    return (
        <Card className={classes.card}>
            <Container className={classes.cardContent}>
                {employee.firstName} {employee.lastName}
            </Container>
        </Card>
    )
}

export default EmployeeCard
