import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { Grow, Container, Grid, Typography, Fab, Slide } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add'
import EditIcon from '@material-ui/icons/Edit'
import EmployeeCard from './employeeCard/EmployeeCard'
import NewEmployeeForm from './employeeForm/EmployeeForm'
import useStyles from './styles'

const EmployeesView = () => {
    const classes = useStyles()
    const employees = useSelector((state) => state.employees)
    const [showEmployeeForm, setShowEmployeeForm] = useState(false)
    const [editMode, setEditMode] = useState(false)
    const [employeeGrid, setEmployeeGrid] = useState(null)

    const handleAddButton = () => {
        setShowEmployeeForm(!showEmployeeForm)
    }

    const unmountEmployeeForm = () => {
        setShowEmployeeForm(false)
    }

    const handleEditButton = (initialValue = false) => {
        const [value, setValue] = React.useState(initialValue);
        const toggle = React.useCallback(() => {
          setValue(v => !v);
        }, []);
        return [value, toggle];
    }

    const displayEmployees = () => {
        setEmployeeGrid(
            <Grid container className={classes.grid} spacing={1}>
                {employees.map((employee) => (
                    <Grid key={employee._id} item>
                        <EmployeeCard employee={employee} edit={editMode}/>
                    </Grid>
                ))}
            </Grid>
        )
    }

    return (
        <Grid className={classes.center}>

            <Grow in>
                {editMode ? employeeGrid : displayEmployees()}
            </Grow>
            <Grid className={classes.buttonTray}>
                <Fab className={classes.button} color="primary" aria-label="addButton" onClick={handleAddButton}>
                    <AddIcon />
                </Fab>
                <Fab className={classes.button} color="primary" aria-label="editButton" onClick={handleEditButton}>
                    <EditIcon />
                </Fab>
            </Grid>
            <Slide direction="up" in={showEmployeeForm} mountOnEnter unmountOnExit>
                <Grid container className={classes.employeeFormContainer} onBlur={unmountEmployeeForm}>
                    <NewEmployeeForm />
                </Grid>
            </Slide>
        </Grid>
    )
}

export default EmployeesView
