import lime from '../lime.svg'
import Button from '@material-ui/core/Button'
import { useState } from 'react'
import { Link } from 'react-router-dom'
import { makeStyles } from '@material-ui/core/styles'
import Card from '@material-ui/core/Card'
import CardActions from '@material-ui/core/CardActions'
import CardContent from '@material-ui/core/CardContent'
import ExpandMoreIcon from '@material-ui/icons/ExpandMore'
import IconButton from '@material-ui/core/IconButton'
import clsx from 'clsx'
import Typography from '@material-ui/core/Typography'
import Grid from '@material-ui/core/Grid'
import TextField from '@material-ui/core/TextField'

const useStyles = makeStyles((theme) => ({
    expand: {
        transform: 'rotate(0deg)',
        marginLeft: 'auto',
        transition: theme.transitions.create('transform', {
          duration: theme.transitions.duration.shortest,
        }),
      },
      expandOpen: {
        transform: 'rotate(180deg)',
      },
}))

const Home = () => {
    const classes = useStyles()
    const [expanded, setExpanded] = useState(false)

    const handleExpandClick = () => {
        setExpanded(!expanded)
    }

    return (
        <Grid
            container
            direction="column"
            alignItems="center"
            justify="center"
            style={{  maxHeight: '60vh', marginTop: '15vh', padding: '1rem'}}
        >
            <Grid item xs={10}>
                <Card style={{ maxWidth: '95vh', backgroundColor: '#424242', borderRadius: '20px'}}>
                    <CardContent>
                        <img src={ lime } className='App-logo' alt='logo' />
                    </CardContent>
                    <CardContent>
                        <Typography color='lightgray' style={{fontSize: '1.5rem', fontWeight: 'bold'}}>
                            SIGN IN
                        </Typography>
                    </CardContent>
                    <IconButton
                        className={clsx(classes.expand, {
                            [classes.expandOpen]: expanded,
                        })}
                            onClick={handleExpandClick}
                            aria-expanded={expanded}
                            aria-label="sign in"
                    >
                        <ExpandMoreIcon color='secondary' />
                    </IconButton>
                </Card>
            </Grid>      
        </Grid>
    )
}

export default Home
