import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { Grow, Container, Grid, Typography, Fab, Slide } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add'
import EditIcon from '@material-ui/icons/Edit'
import ArrowDownwardIcon from '@material-ui/icons/ArrowDownward'
import EmployeeCard from './employeeCard/EmployeeCard'
import NewEmployeeForm from './employeeForm/EmployeeForm'
import useStyles from './styles'

const EmployeesView = () => {
    const classes = useStyles()
    const employees = useSelector((state) => state.employees)
    const [showEmployeeForm, setShowEmployeeForm] = useState(false)
    const [showEmployeeGrid, setShowEmployeeGrid] = useState(true)
    const [showEmployees, setShowEmployees] = useState(
        <Grid container className={classes.grid} spacing={1}>
            {employees.map((employee) => (
                <Grid key={employee._id} item>
                    <EmployeeCard employee={employee} edit={false}/>
                </Grid>
            ))}
        </Grid>
    )
    const [editMode, setEditMode] = useState(false)

    const handleAddButton = () => {
        showEmployeeForm ? unmountEmployeeForm() : mountEmployeeForm()
    }

    const mountEmployeeForm = () => {
        setTimeout(function(){setShowEmployeeForm(!showEmployeeForm)},250)
        setShowEmployeeGrid(!showEmployeeGrid)
    }

    const unmountEmployeeForm = () => {
        setShowEmployeeForm(!showEmployeeForm)
        setTimeout(function(){setShowEmployeeGrid(!showEmployeeGrid)},250)
    }

    const handleEditButton = () => {
        setEditMode((prev) => setEditMode(!prev))

        if (setShowEmployeeForm) {
            setTimeout(function() {
                setShowEmployeeForm(false)

            }, 300)
        }

        setTimeout(function()
        {
            setShowEmployeeGrid(true)
            editMode ? 
                setShowEmployees(displayEditEmployees()) : 
                setShowEmployees(displayEmployees())
            }
        ,300)
        setShowEmployeeGrid(false)
    }

    const displayEmployees = () => {
        return (
            <Grid container spacing={1}>
                {employees.map((employee) => (
                    <Grid key={employee._id} item>
                        <EmployeeCard employee={employee} edit={false}/>
                    </Grid>
                ))}
            </Grid>
        )
    }
    const displayEditEmployees = () => {
        return (
            <Grid container direction="column" justify="space-around" alignItems="center">
                <Grid container spacing={1}>
                    {employees.map((employee) => (
                        <Grid key={employee._id} item>
                            <EmployeeCard employee={employee} edit={true}/>
                        </Grid>
                    ))}
                </Grid>
            </Grid>
        )
    }

    return (
        <div style={{ padding: 20 }}>
            <Grid container direction="column" justify="space-around" alignItems="center">

                <Slide direction ='down' in={showEmployeeGrid} mountOnEnter unmountOnExit>
                    {showEmployees}
                </Slide>

                <Grid className={classes.buttonTray}>
                    <Fab className={classes.button} color="primary" aria-label="addButton" onClick={handleAddButton}>
                        { showEmployeeForm ? <ArrowDownwardIcon/> : <AddIcon/> }
                    </Fab>
                    <Fab className={classes.button} color="primary" aria-label="editButton" onClick={ () => handleEditButton() }>
                        <EditIcon />
                    </Fab>
                </Grid>

                <Slide direction="up" in={showEmployeeForm} mountOnEnter unmountOnExit>
                    <Grid item sm={12} className={classes.employeeFormContainer} >
                        <NewEmployeeForm />
                    </Grid>
                </Slide>

            </Grid>
        </div>
    )
}

export default EmployeesView
