        //position: 'absolute',
        //right: 100,
        //top: 100,
        width: 400,
        height: 400,
        borderRadius: 20,
        zIndex: 0,
        [theme.breakpoints.down('sm')]: {
            //marginTop: 30,
            width: 300,
            height: 300,
        },
        [theme.breakpoints.down('xs')]: {
            //marginTop: 30,
            width: 50,
            height: 50,
        },
    },
    avacado: {
        //position: 'absolute',
        width: 200,
