import { useState } from 'react'
import { makeStyles } from '@material-ui/core'
import ReactCardFlip from 'react-card-flip'
import Card from '@material-ui/core/Card'
import Container from '@material-ui/core/Container'
import EmployeesView from '../EmployeesPage'
import Divider from '@material-ui/core/Divider'
import Button from '@material-ui/core/Button'
import Typography from '@material-ui/core/Typography'

const useStyles = makeStyles((theme) => ({
    card: {
        height: '200px',
        margin: '10px',
        width: '200px',
        borderRadius: '10px',
        boxShadow: '0 5px 40px -12px rgba(0,0,0,0.8)',
        transition: '0.1s',
        '&:hover button' : {
            boxShadow: '0 10px 70px -12.125px rgba(0,0,0,0.8)',
            opacity: '1',
        }
    },
    cardTop: {
        height: '150px',
        justifyContent: 'center',
        textAlign: 'center',
        margin:'auto',
        alignContent: 'center',
        fontSize: '18px',
    },
    cardBottom: {
        height: '50px',
        justifyContent: 'center',
        textAlign: 'center',
        margin:'auto',
        alignContent: 'center',
    },
    cardBack: {
        height: '250px',
        width: '200px',
        justfiyContent: 'center',
        alignContent: 'center',
        textAlign: 'center',
        margin: '10px',
        marginTop:'-25px',
        marginBottom:'-25px',
        borderRadius: '10px',
        boxShadow: '0 10px 70px -12.125px rgba(0,0,0,0.8)',
        '&:hover button' : {
            boxShadow: '0 10px 70px -12.125px rgba(0,0,0,0.8)',
            opacity: 1,
        }

    },
    flipButton: {
        color: theme.palette.secondary.main,
        opacity: 0,
        transition: '0.1s',
        height: '30px',
        width: '90px',
        fontSize: '12px',
    },
}))

const EmployeeCard = ({employee}) => {
    const classes = useStyles()
    const [isFlipped, setIsFlipped] = useState()

    return (
        <ReactCardFlip isFlipped={isFlipped} flipDirection='vertical'>

            <Card className={classes.card}>
                <Container className={classes.cardTop}>
                    {employee.firstName} {employee.lastName}
                    <Divider />
                </Container>
                <Container className={classes.cardBottom}>
                    <Typography>
                        {employee.position}
                    </Typography>
                    <Button 
                        className={classes.flipButton} 
                        variant='text'
                        style={{top: '10px'}}
                        onClick={() => setIsFlipped(!isFlipped)}
                    >
                        Flip Card
                    </Button>
                </Container>
            </Card>
            <Card className={classes.cardBack}>
                <Typography>
                    {employee.email}
                </Typography>
                <Button 
                    className={classes.flipButton} 
                    style={{top: '190px'}}
                    onClick={() => setIsFlipped(!isFlipped)}
                >
                        Flip Card
                </Button>
            </Card>
        </ReactCardFlip>
    )
}

export default EmployeeCard
