import { Button } from "@material-ui/core"
import { useState } from "react"
import { useDispatch, useSelector } from "react-redux"
import { editEmployee } from '../../actions/employees'

const GenerateScheduleButton = ({employees}) => {
    const dispatch = useDispatch()
    const [employee, setEmployee] = useState(employees[0])
    const [currentId, setCurrentId] = useState(employees[0]._id)
    const [weekSchedule, setWeekSchedule] = useState(employees[0].weekSchedule)
    const newWeekSchedule = [
        {
        title: 'Cold Line',
        startDate: '2021-04-19T08:30', 
        endDate: '2021-04-19T16:00', 
        },
        {
        title: 'Hot Line',
        startDate: '2021-04-20T16:00', 
        endDate: '2021-04-20T22:30', 
        },
    ]

    const createSchedule = () => {
        setWeekSchedule(
            [
                {
                title: 'Cold Line',
                startDate: '2021-04-19T08:30', 
                endDate: '2021-04-19T16:00', 
                },
                {
                title: 'Hot Line',
                startDate: '2021-04-20T16:00', 
                endDate: '2021-04-20T22:30', 
                },
            ]
        )
        setEmployee({...employee, weekSchedule: weekSchedule})
    }

    const handleSubmit = () => {
        createSchedule()
        console.log(employee)
        dispatch(editEmployee(currentId, employee))
    }


    return (
        <Button color='secondary' style={{width: 300, height: 50, margin:50}} variant='contained' onClick={handleSubmit}>
            Generate Schedule
        </Button>

    )
}

export default GenerateScheduleButton
