import { useState, useEffect } from 'react'
import { useSelector, useDispatch } from 'react-redux'
import { Grow, Container, Grid, Typography, Fab, Slide } from '@material-ui/core'
import AddIcon from '@material-ui/icons/Add'
import EditIcon from '@material-ui/icons/Edit'
import EmployeeCard from './employeeCard/EmployeeCard'
import NewEmployeeForm from './employeeForm/EmployeeForm'
import useStyles from './styles'

const EmployeesView = () => {
    const classes = useStyles()
    const employees = useSelector((state) => state.employees)
    const [showEmployeeForm, setShowEmployeeForm] = useState(false)
    const [showEmployeeGrid, setShowEmployeeGrid] = useState(true)
    const [editMode, setEditMode] = useState(false)

    const handleAddButton = () => {
        // setShowEmployeeGrid(!showEmployeeGrid)

            setShowEmployeeForm(!showEmployeeForm)

    }

    const unmountEmployeeForm = () => {
        setTimeout(function(){
            setShowEmployeeForm(!showEmployeeForm)
        },500)
        setShowEmployeeGrid(!showEmployeeGrid)
    }

    const handleEditButton = () => {
        setEditMode(!editMode)
        displayEmployees()
    }

    const displayEmployees = () => {
        return (
            <Grid container className={classes.grid} spacing={1}>
                {employees.map((employee) => (
                    <Grid key={employee._id} item>
                        <EmployeeCard employee={employee} edit={false}/>
                    </Grid>
                ))}
            </Grid>
        )
    }
    const displayEditEmployees = () => {
        return (
            <Grid container className={classes.grid} spacing={1}>
                {employees.map((employee) => (
                    <Grid key={employee._id} item>
                        <EmployeeCard employee={employee} edit={true}/>
                    </Grid>
                ))}
            </Grid>
        )
    }

    return (
        <Grid className={classes.center}>

            <Slide direction ='down' in={showEmployeeGrid} mountOnEnter unmountOnExit>
                {displayEmployees()}
            </Slide>
            <Grid className={classes.buttonTray}>
                <Fab className={classes.button} color="primary" aria-label="addButton" onClick={handleAddButton}>
                    <AddIcon />
                </Fab>
                <Fab className={classes.button} color="primary" aria-label="editButton" onClick={unmountEmployeeForm}>
                    <EditIcon />
                </Fab>
            </Grid>
            <Slide direction="up" in={showEmployeeForm} mountOnEnter unmountOnExit>
                <Grid container className={classes.employeeFormContainer} onBlur={unmountEmployeeForm}>
                    <NewEmployeeForm />
                </Grid>
            </Slide>
        </Grid>
    )
}

export default EmployeesView
