import { makeStyles } from '@material-ui/core'

export default makeStyles((theme) => ({
    center: {
        display: 'flex',
        flexDirection: 'column',
        alignItems: 'center',
        justifyContent: 'center',
    },
    title: {
        margin: '2rem',
        color: '#F7F7FF',
    },
    grid: {
        flexGrow: 5,
        justifyContent: 'center',
        alignItems: 'center',
        marginBottom: 70,
    },
    buttonTray: {
        // display: 'flex',
        // flexDirections: 'row',
        // justifyContent: 'center',
        // alignItems: 'center',
        margin: 5,
        right: 20,
        bottom: 20,
        position: 'fixed',
        zIndex: 999,
        [theme.breakpoints.down('sm')]: {
            position: 'fixed',
            bottom:20,
            left: '50%',
            right: 'auto',
            marginLeft: '-50vw',
            display: 'flex',
            flexDirections: 'row',
            justifyContent: 'center',
            alignItems: 'center',
        }
    },
    employeeFormContainer: {
        [theme.breakpoints.down('sm')]: {
            paddingTop: 50,
            paddingBottom: 100,
        }
        // width: 'auto',
        // height: 'auto',
        // marginTop: '-50px',
        // marginLeft: '-250px',
        // left: '43%',
        // position: 'absolute',
        // marginLeft: '50%',
        // marginRight: '50%',
        // left: '50%',
        // right: '50%',
        // paddingTop: 50,
        // paddingBottom: 90,
        // [theme.breakpoints.down('sm')]: {
        //     left: 'auto',
        //     right: 'auto',
        // }
    },
    button: {
        margin:10,
    },
}))
