const date = new Date()
let monday = new Date()

switch (date.getDay()) {
    case 0:
        break;
    case 1:
        break;
    case 2:
        break;
    case 3:
        break;
    case 4:
        break;
    case 5:
        console.log("hello");
        monday = new Date(monday.setDate(monday.getDate() - 4 + 7))
        break;
    case 6:
        break;
}


const shifts =
        [
            {
                title: `${date}`,
                startTime: `${monday = new Date(monday.setHours(7, 30, 0))}`,
                endTime: `${monday = new Date(monday.setHours(16,0,0))}`
            }
        ]

export default shifts
