import StyledTextField from './styledComponents/StyledTextField.js'
import Card from '@material-ui/core/Card'
import Grid from '@material-ui/core/Grid'
import Button from '@material-ui/core/Button'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import Slide from '@material-ui/core/Slide'
import Icon from '@material-ui/core/Icon'
import IconButton from '@material-ui/core/IconButton'
import { useState, forwardRef } from 'react'
import StyledButton from './styledComponents/StyledButton.js'


const Transition = forwardRef(function Transition(props, ref) {
    return <Slide direction="up" ref={ref} {...props} />;
});

export default function AddEmployeeButton({employees, addEmployee}) {
    const [open, setOpen] = useState(false);

    const handleClickOpen = () => {
        setOpen(true);
    };

    const handleClose = () => {
        setOpen(false);
    };

    return (
        <div>
            <IconButton style={{bottom: '2vh'}} onClick={handleClickOpen}>
                    <Icon color='secondary'>add-icon</Icon>
            </IconButton>
            <Dialog
                color='primary'
                open={open}
                TransitionComponent={Transition}
                keepMounted
                onClose={handleClose}
                aria-labelledby="alert-dialog-slide-title"
                aria-describedby="alert-dialog-slide-description"
            >
                <DialogTitle id="alert-dialog-slide-title"><h1>Add Employee</h1></DialogTitle>
                <DialogContent>
                    <DialogContentText id="alert-dialog-slide-description">
                    <Card>
                        <Grid
                            container
                            direction="column"
                            alignItems="center"
                            justify="center"
                            style={{ backgroundColor: '#424242', margin: '100', padding: '0'}}
                        >
                            <form noValidate autoComplete='off'>
                                <div>
                                    <StyledTextField 
                                        label='First Name' 
                                        required
                                        variant='filled'
                                        color='secondary'
                                    />
                                </div>
                                <div>
                                    <StyledTextField 
                                        label='M.I.' 
                                        inputProps={{ maxLength: '1'}}
                                        variant='filled'
                                        color='secondary'
                                    />
                                </div>
                                <div>
                                    <StyledTextField 
                                        label='Last Name' 
                                        required
                                        variant='filled'
                                        color='secondary'
                                    />
                                </div>
                                <div>
                                    <StyledTextField 
                                        label='Phone Number' 
                                        required
                                        variant='filled'
                                        color='secondary'
                                    />
                                </div>
                                <div>
                                    <StyledTextField 
                                        label='Email' 
                                        required
                                        variant='filled'
                                        color='secondary'
                                    />
                                </div>
                            </form>
                        </Grid>
                    </Card>
                    </DialogContentText>
                </DialogContent>
                <DialogActions>
                    <Button onClick={handleClose} color="primary">
                        Cancel
                    </Button>
                    <StyledButton onClick={addEmployee} color="secondary">
                        Save Employee
                    </StyledButton>
              </DialogActions>
            </Dialog>
        </div>
    );
}





