import { Card, Typography, TextField, Button, MenuItem } from "@material-ui/core"
import { useState } from 'react'
import useStyles from './styles'
import { useDispatch } from 'react-redux'
import { addEmployee } from '../../../actions/employees'
import PhoneField from './phoneNumberInput/PhoneNumberInput'
import { HOTLINE, COLDLINE, MAINTENANCE, FLOATER, BACKUPCASH, CASHIER, MEATPREP } from '../../../constants/positions'


const positions = [HOTLINE, COLDLINE, MAINTENANCE, FLOATER, BACKUPCASH, CASHIER, MEATPREP]

const EmployeeForm = () => {
    const classes = useStyles()
    const dispatch = useDispatch()

    const [employee, setEmployee] = useState({
        firstName: '',
        middleInitial: '',
        lastName: '',
        phoneNumber: '',
        email: '',
        positions: MAINTENANCE,
        availability: [
            { 
                id: 1,
                startDate: '2021-03-15T07:30',
                endDate: '2021-03-15T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 2,
                startDate: '2021-03-16T07:30',
                endDate: '2021-03-16T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 3,
                startDate: '2021-03-17T07:30',
                endDate: '2021-03-17T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 4,
                startDate: '2021-03-18T07:30',
                endDate: '2021-03-18T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 5,
                startDate: '2021-03-19T07:30',
                endDate: '2021-03-19T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 6,
                startDate: '2021-03-20T07:30',
                endDate: '2021-03-20T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 0,
                startDate: '2021-03-21T07:30',
                endDate: '2021-03-21T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
        ],
    }) 

    const handleSubmit = (e) => {
        e.preventDefault()

        dispatch(addEmployee(employee))
        clear()
    }

    const clear = () => {
        setEmployee({firstName: '', middleInitial: '', lastName: '', phoneNumber: '', email: '', positions: MAINTENANCE})
    }

    return (
        <Card className={classes.card} >
            <form className={`${classes.root} ${classes.form}`} autoComplete="off" noValidate onSubmit={handleSubmit} >
                {/* <Typography variant="h6">{currentId ? 'Editing' : 'Creating'} a Memory</Typography> */}
                <TextField name="firstName" required variant="outlined" label="First Name" fullWidth value={employee.firstName} onChange={ (e) => setEmployee({ ...employee, firstName: e.target.value }) } />
                <TextField name="middleInitial" variant="outlined" label="Middle Initial" fullWidth value={employee.middleInitial} onChange={ (e) => setEmployee({ ...employee, middleInitial: e.target.value }) } />
                <TextField name="lastName" required variant="outlined" label="Last Name" fullWidth value={employee.lastName} onChange={ (e) => setEmployee({ ...employee, lastName: e.target.value}) } />
                <PhoneField  value={employee.phoneNumber} onChange={ (e) => setEmployee({ ...employee, phoneNumber: e.target.value}) } />
                <TextField name="email" variant="outlined" label="Email" fullWidth value={employee.email} onChange={ (e) => setEmployee({...employee, email: e.target.value}) } />
                <TextField select name="position" variant="outlined" label="Position" fullWidth value={employee.positions} onChange={ (e) => setEmployee({...employee, positions: e.target.value }) } >
                    {positions.map((option) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>
                <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large"  type='submit' fullWidth>Submit</Button>
                {/* <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button> */}
            </form>
        </Card>
    )
}

export default EmployeeForm
