import StyledTextField from './styledComponents/StyledTextField.js'
import Card from '@material-ui/core/Card'
import Box from '@material-ui/core/Box'
import Button from '@material-ui/core/Button'
import Dialog from '@material-ui/core/Dialog'
import DialogActions from '@material-ui/core/DialogActions'
import DialogContent from '@material-ui/core/DialogContent'
import DialogContentText from '@material-ui/core/DialogContentText'
import DialogTitle from '@material-ui/core/DialogTitle'
import Slide from '@material-ui/core/Slide'
import Icon from '@material-ui/core/Icon'
import IconButton from '@material-ui/core/IconButton'
import { useState, forwardRef, useEffect, useRef} from 'react'
import { useTheme } from '@material-ui/core/styles'
import useMediaQuery from '@material-ui/core/useMediaQuery'
import StyledButton from './styledComponents/StyledButton.js'
import { SportsRugbySharp } from '@material-ui/icons'

export default function ScrollDialog() {
    const [open, setOpen] = useState(false);
    const [scroll, setScroll] = useState('paper');

    const handleClickOpen = (scrollType) => () => {
      setOpen(true);
      setScroll(scrollType);
    };

    const handleClose = () => {
      setOpen(false);
    };

    const descriptionElementRef = useRef(null);
    useEffect(() => {
      if (open) {
        const { current: descriptionElement } = descriptionElementRef;
        if (descriptionElement !== null) {
          descriptionElement.focus();
        }
      }
    }, [open]);

    return (
      <div>
        <IconButton style={{bottom: '2vh'}} onClick={handleClickOpen('paper')}>
            <Icon color='secondary'>add-icon</Icon>
        </IconButton>
        <Dialog
          open={open}
          onClose={handleClose}
          scroll={scroll}
          aria-labelledby="scroll-dialog-title"
          aria-describedby="scroll-dialog-description"
        >
        <DialogTitle 
            id="scroll-dialog-title"
            style={{
                backgroundImage:'linear-gradient(0deg, #353535 30%, #424242 120%)',
                 //backgroundColor: '#424242',
                 //background: 'rgba(30,30,30,0.0)',
            }}
        >
            <h3>New Employee</h3>
        </DialogTitle>
            <DialogContent 
                dividers={scroll === 'paper'}
                // style={{backgroundImage:'linear-gradient(25deg, #ccc 60%, #bbb 120%)'}}
                style={{backgroundColor:'#424242'}}

            >
            <DialogContentText
              id="scroll-dialog-description"
              ref={descriptionElementRef}
              tabIndex={-1}
            >
              <form width='95%' noValidate autoComplete='off'>
                    <div>
                        <StyledTextField 
                            label='First Name' 
                            required
                            variant='filled'
                            color='secondary'
                        />
                    </div>
                    <div>
                        <StyledTextField 
                            label='M.I.' 
                            inputProps={{ maxLength: '1'}}
                            variant='filled'
                            color='secondary'
                        />
                    </div>
                    <div>
                        <StyledTextField 
                            label='Last Name' 
                            required
                            variant='filled'
                            color='secondary'
                        />
                    </div>
                    <div>
                        <StyledTextField 
                            label='Phone Number' 
                            required
                            variant='filled'
                            color='secondary'
                        />
                    </div>
                    <div>
                        <StyledTextField 
                            label='Email' 
                            required
                            variant='filled'
                            color='secondary'
                        />
                    </div>
                </form>
            </DialogContentText>
          </DialogContent>
          <DialogActions style={{backgroundColor:'#424242'}}>
            <Button variant='contained' onClick={handleClose} color='primary'>
              Cancel
            </Button>
            <Button variant='contained' onClick={handleClose} color='secondary'>
              Add Employee
            </Button>
          </DialogActions>
        </Dialog>
      </div>
    )
}
