import './App.css';
import Home from './components/Home.js'
import NavBar from './components/NavBar.js'
import SignIn from './components/SignIn.js'
import Schedule from './components/Schedule.js'
import EditEmployee from './components/EditEmployee.js'
import EmployeesView from './components/EmployeesView.js'
import Employee from './components/Employee.js'
import theme from './components/theme.js'
import {  ThemeProvider } from '@material-ui/core/styles'
import { BrowserRouter as Router, Route, Link, Redirect } from 'react-router-dom'
import { useState } from 'react'

function App() {
  const [employees, setEmployees ] = useState([<Employee />])
  const isUserAuthenticated = false

  const addEmployee = () => {
    const empId = employees.length
    const newEmployee = <Employee empId={empId} />
    setEmployees([...employees, newEmployee])
  }

  const removeEmployee = () => {
    const temp = [...employees]
    temp.splice(0,1)
    setEmployees(temp)
  }

  return (
    <Router>
      <ThemeProvider theme={ theme }>
        <div className='App'>
          <header className='NavBar'>
            <NavBar />
          </header>
          <Route
            exact
            path="/"
            render={() => {
                return (
                  isUserAuthenticated ?
                  <Redirect to="/home" /> :
                  <Redirect to="/sign-in" /> 
                )
            }}
          />
          <Route path='/sign-in' component={ SignIn } />
          <Route path='/home' component={ Home } />
          <Route path='/schedule' component={ Schedule } />
          <Route path='/editEmployee' component={ EditEmployee } />
          <Route path='/employees'> <EmployeesView addEmployeeButton={() => addEmployee()}/> </Route>
        </div>
      </ThemeProvider>
    </Router>
  );
}

export default App;
