import { makeStyles } from '@material-ui/core/styles'
import { 
    ViewState, 
    EditingState, 
    IntegratedEditing 
} from '@devexpress/dx-react-scheduler'
import { 
    Scheduler, 
    DayView, 
    WeekView,
    Appointments, 
    AppointmentForm,
    ConfirmationDialog,
    AppointmentTooltip,
    Toolbar,
    ViewSwitcher,
    TodayButton,
    DateNavigator,
} from '@devexpress/dx-react-scheduler-material-ui'
import Card from '@material-ui/core/Card'
import { appointments } from '../testData/testTimes'


const useStyles = makeStyles((theme) => ({
    calendarCard: {
        width: '95vw',
        margin: '2rem',
        padding: '1rem',
        borderRadius: '20px',
        boxShadow: '0px 3px 3px 0px rgba(30, 30, 30, .7)',
    },
}))

const Calendar = () => {
    const classes = useStyles()
    const currentDate = new Date()
    const schedulerData = appointments

    return (
        <Card className={classes.calendarCard}>
            <Scheduler
                data={schedulerData}
            >
                <ViewState
                    defaultCurrentDate={currentDate}
                />
                <DayView
                    startDayHour={7}
                    endDayHour={23}
                />
                <WeekView
                    startDayHour={7}
                    cellDuration={60}
                    endDayHour={23}
                />
                <Toolbar />
                <ViewSwitcher />
                <Appointments />
                <AppointmentTooltip 
                    showCloseButton
                    showDeleteButton
                />
                <TodayButton />
                <DateNavigator />
            </Scheduler>
        </Card>
    )
}

export default Calendar
