import { makeStyles } from '@material-ui/core'

export default makeStyles((theme) => ({
    card: {
        height: 200,
        width: 200,
        margin: 3,
        borderRadius: '10px',
        boxShadow: '0 5px 40px -12px rgba(0,0,0,0.8)',
        transition: '0.1s',
        '&:hover' : {
            boxShadow: '0 10px 70px -12.125px rgba(0,0,0,0.8)',
        },
    },
    editCard: {
        height: 200,
        width: 200,
        borderRadius: '10px',
        margin:3,
    },
    cardFrontTop: {
        height: '50px',
        justifyContent: 'center',
        textAlign: 'center',
        margin:'auto',
        alignContent: 'center',
    },
    cardTitle: {
        adjustFontSizeToFit:'true', 
        numberOfLines:1, 
        fontSize: 20, 
        fontWeight: 400
    },
    cardFrontDetails: {
        height: '120px',
        width: 200,
        display: 'flex',
        flexDirection: 'reverse-column',
        justfiyContent: 'space-between',
        alignItems: 'center',
        textAlign: 'center',
        fontSize: '14px'
    },
    cardBack: {
        height: 200,
        width: 200,
        margin: 3,
        borderRadius: '10px',
        boxShadow: '0 10px 70px -12.125px rgba(0,0,0,0.8)',
        '&:hover' :{
            boxShadow: '0 10px 70px -12.125px rgba(0,0,0,0.8)',
        },
    },
    cardBackContent: {
        display: 'flex',
        flexDirection: 'column',
        justfiyContent: 'center',
        alignItems: 'center',
        textAlign: 'center',
    },
    flipButtonFrontArea: {
        justifyContent: 'center',
        alignItems: 'center',
        display: 'flex',
        height: 40,
    },

    flipButton: {
        color: theme.palette.secondary.main,
        opacity: 0,
        margin:0,
        padding:0,
        transition: '0.1s',
        height: '25px',
        width: '90px',
        fontSize: '12px',
        [theme.breakpoints.down('xs')]: {
            opacity: 1,
        },
    },
    divider: {
        backgroundColor: theme.palette.primary.main,
    }
}))
