import { Button, Typography, Grid } from "@material-ui/core"
import { useState, useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { editEmployee } from '../../actions/employees'
import weekShifts from '../../testData/weekShifts'


const GenerateScheduleButton = () => {
    let shifts = weekShifts
    const dispatch = useDispatch()
    const employees = useSelector((state) => state.employees)
    const [employee, setEmployee] = useState(null)
    const [currentId, setCurrentId] = useState(null)
    //const length = weekShifts.length

    const randomizeShifts = (array) => {
        const shuffledArray = []

        let shift = null

        for (let i = array.length - 1; i >= 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
            shift = array[i]

            for (let x = shift.length - 1; x >= 0; x--) {
                const y = Math.floor(Math.random() * (x + 1));
                [array[i][x], array[i][y]] = [array[i][y], array[i][x]];
                shift = array[i][x]
                shuffledArray.push(shift)
            }
        }
        return shuffledArray
    }

    const createSchedule = (shift) => {
        const startTime = shift.startDate.split(' ').[4]
        console.log(startTime)
        setEmployee(employees[0])
        console.log(employee)
        // setCurrentId(employees[0]._id)
        // setEmployee({...employee, weekSchedule:[
        //     {
        //     title: 'Cold Line',
        //     startDate: '2021-04-26T08:30', 
        //     endDate: '2021-04-26T16:00', 
        //     },
        //     {
        //     title: 'Hot Line',
        //     startDate: '2021-04-27T16:00', 
        //     endDate: '2021-04-27T22:30', 
        //     },
        //     {
        //     title: 'Cold Line',
        //     startDate: '2021-04-28T08:30', 
        //     endDate: '2021-04-28T16:00', 
        //     },
        //     {
        //     title: 'Cold Line',
        //     startDate: '2021-04-28T16:00', 
        //     endDate: '2021-04-28T22:30', 
        //     },
        //     {
        //     title: 'Floater',
        //     startDate: '2021-04-29T15:30', 
        //     endDate: '2021-04-29T22:30', 
        //     },
        //     {
        //     title: 'Floater',
        //     startDate: '2021-04-30T15:30', 
        //     endDate: '2021-04-30T22:30', 
        //     },
        // ]})
    }

    const handleSubmit = (e) => {
        e.preventDefault()
        const randomShiftsArray = randomizeShifts(shifts)
        console.log(randomShiftsArray[0])
        createSchedule(randomShiftsArray[0])
        //dispatch(editEmployee(currentId, employee))
    }


    return (
        <Grid container direction='column' justify='center' alignItems='center'>
            <Button color='secondary' style={{width: 300, height: 50, margin:50}} variant='contained' onClick={handleSubmit}>
                Generate Schedule
            </Button>
        </Grid>
    )
}

export default GenerateScheduleButton
