import './App.css';
import Home from './components/HomePage.js'
import NavBar from './components/NavBar.js'
import SignIn from './components/SignInPage.js'
import Schedule from './components/SchedulePage.js'
import CssBaseline from '@material-ui/core/CssBaseline'
import EmployeesView from './components/EmployeesPage.js'
import Employee from './components/styledComponents/Employee.js'
import theme from './components/theme.js'
import {  ThemeProvider } from '@material-ui/core/styles'
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom'
import { useState } from 'react'
import { testEmployees } from './testData/testEmployees'
import { appointments } from './testData/testTimes'
import { Container } from '@material-ui/core';

function App() {
  const [employees, setEmployees ] = useState(testEmployees)

  const isUserAuthenticated = false
  const [count, setCount] = useState(0)

  const setEmployee = ({employee}) => {
    const newEmployee = {
      firstName: employee.firstName,
      middleInitial: employee.middleInitial,
      lastName: employee.lastName,
      email: employee.email,
      phoneNumber: employee.phoneNumber,
      tenure: employee.startDate,
      position: [employee.position],
      availability: employee.availability,
      desiredHours: employee.desiredHours,
    }
    setEmployees([...employees, newEmployee])
  }

  const editEmployee = () => {

  }

  const removeEmployee = () => {
    const temp = [...employees]
    temp.splice(0,1)
    setEmployees(temp)
  }

  const homeButton = () => {
    setCount(count+1)
  }

  return (
    <Router>
      <Container style={{
          // background: 'linear-gradient(to right, #a8ff78, #78ffd6)',
          background: 'linear-gradient(to right, #636363, #a2ab58)',
          margin: 0,
          padding: 0,
          minHeight: '100vh',
        }}>

      <ThemeProvider theme={ theme }>
        <CssBaseline />
          <NavBar/>

          <Route
            exact
            path="/"
            render={() => {
                return (
                  isUserAuthenticated ?
                  <Redirect to="/home" /> :
                  <Redirect to="/sign-in" /> 
                )
            }}
          />
          <Route path='/sign-in' component={ SignIn } />
          {/* <Route path='/home' component={ Home } /> */}
          <Route path='/home'>
            <Home count={employees.length} click={homeButton}/>
          </Route>
          <Route path='/schedule'>
            <Schedule data={appointments} />
          </Route>
          <Route path='/employees'>
            <EmployeesView employees={employees} addEmployee={setEmployee} />
          </Route>  

      </ThemeProvider>
      </Container>
    </Router>
  );
}

export default App;
