import { Button, Typography, Grid } from "@material-ui/core"
import { useState, useEffect } from "react"
import { useDispatch, useSelector } from "react-redux"
import { editEmployee, getEmployees } from '../../actions/employees'
import weekShifts from '../../testData/weekShifts'


const GenerateScheduleButton = () => {
    let shifts = weekShifts
    const dispatch = useDispatch()
    const employees = useSelector((state) => state.employees)
    const [employee, setEmployee] = useState(null)
    const [schedule, setSchedule] = useState(null)
    const [currentId, setCurrentId] = useState(null)

    // useEffect(() => {
    //     updateEmployee(employee)
    // })


    const randomizeShifts = (array) => {
        let shuffledArray = []

        let shift = null

        for (let i = array.length - 1; i >= 0; i--) {
            const j = Math.floor(Math.random() * (i + 1));
            [array[i], array[j]] = [array[j], array[i]];
            shift = array[i]

            for (let x = shift.length - 1; x >= 0; x--) {
                const y = Math.floor(Math.random() * (x + 1));
                [array[i][x], array[i][y]] = [array[i][y], array[i][x]];
                shift = array[i][x]
                shuffledArray.push(shift)
            }
        }
        return shuffledArray
    }


    const createSchedule = (shifts) => {
        const updatedEmployees = employees
        while(shifts.length > 0) {
            let currentEmployee = updatedEmployees[Math.floor(Math.random() * (updatedEmployees.length))]
            const shift = shifts[0]
            const shiftStartTime = convertTimeToNumber(shifts[0].startDate.split(' ').[4])

            if(canEmployeeTakeShift(currentEmployee, shift, shiftStartTime)) {
                currentEmployee.weekSchedule.push(shift)
                shifts.splice(shift, 1)
            }

            // setSchedule({...schedule, shift})

            // setEmployee({...currentEmployee, weekSchedule:[
            //     schedule
            // ]})

        }
        console.log(updatedEmployees)

    }


    const convertTimeToNumber = (time) => {
        var hoursMinutes = time.split(/[.:]/);
        var hours = parseInt(hoursMinutes[0], 10);
        var minutes = hoursMinutes[1] ? parseInt(hoursMinutes[1], 10) : 0;
        return hours + minutes / 60;
    }


    const canEmployeeTakeShift = (employee, shift, shiftStartTime) => {
        let isAbleToWork = Boolean
        const shiftDay = shift.startDate.split(' ').[0]
        const isMorningShift = shiftStartTime < 12 ? true : false
        const isEmployeeAvailable = () => {
            employee.availability[0] ?
            true :
            false
        }

        if (employee) {
            isAbleToWork = true
        }
        return isAbleToWork
    }


    const handleSubmit = (e) => {
        e.preventDefault()
        const randomShiftsArray = randomizeShifts(shifts)
        createSchedule(randomShiftsArray)
        console.log(employees[0].availability[0].day)
        //dispatch(editEmployee(employee._id, employee))
    }


    return (
        <Grid container direction='column' justify='center' alignItems='center'>
            <Button color='secondary' style={{width: 300, height: 50, margin:50}} variant='contained' onClick={handleSubmit}>
                Generate Schedule
            </Button>
        </Grid>
    )
}

export default GenerateScheduleButton
