import { makeStyles } from '@material-ui/core'
import abstractImage from '../../images/Abstract-Envelope.svg'


export default makeStyles((theme) => ({
    root: {
        flexGrow: 1,
        width: '75vw',
        //maxHeight: '80vw',
        // display: 'flex',
        // flexDirection: 'column',
        // alignItems:'center',
        // justifyContent:'center',
        [theme.breakpoints.down('xs')]: {
            width: '100vw',

        }
    },
    homeDisplay: {
        //display: 'flex',
        flexDirecton: 'column',
        alignItems: 'center',
        justifyContent: 'center',
    },
    cardDisplay: {
        width: 150,
        height: 110,
        borderRadius: 10,
        zIndex: 0,
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignContent: 'center',
        margin: 50,
        //backgroundColor: theme.palette.primary.main,
        boxShadow: '0 5px 40px -12px rgba(0,0,0,0.8)',
        [theme.breakpoints.down('sm')]: {
            width: 120,
            height: 100,
        },
        [theme.breakpoints.down('xs')]: {
            width: 120,
            height: 100,
        },
    },
    titleCard: {
        width: 311,
        height: 110,
        borderRadius: 10,
        boxShadow: '0 5px 40px -12px rgba(0,0,0,0.8)',
        display: 'flex',
        direction: 'row',
        alignItems: 'center',
    },
    title: {
        fontSize: 29,
        fontWeight: 900,
        textTransform: 'uppercase',
    },
    sloganCard: {
        width: 311,
        height: 110,
        borderRadius: 10,
        boxShadow: '0 5px 40px -12px rgba(0,0,0,0.8)',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
    },
    slogan: {
        fontSize: 18,
        fontWeight: 300,
        marginLeft: 15,
    },
    logo: {
        width: 90,
        margin: 5,
    },
}))
