import { useState } from 'react'
import { makeStyles } from '@material-ui/core/styles'
import { ViewState, EditingState, IntegratedEditing } from '@devexpress/dx-react-scheduler'
import { 
    Scheduler,  
    WeekView,
    Appointments, 
    AppointmentTooltip,
    DragDropProvider,
    ConfirmationDialog,
    EditRecurrenceMenu,
    AllDayPanel,
} from '@devexpress/dx-react-scheduler-material-ui'
import { areIntervalsOverlapping } from 'date-fns'

const Availability = ({employeeAvailability}) => {
    const currentDate = new Date()
    const [availability, setAvailability] = useState(employeeAvailability)

    const commitChanges = ({ added, changed, deleted }) => {
          if (added) {
            const startingAddedId = availability.length > 0 ? availability[availability.length - 1].id + 1 : 0;
            setAvailability([...availability, { ...added }])
          }
          if (changed) {
            setAvailability(availability.map(appointment => (
              changed[appointment.id] ? { ...appointment, ...changed[appointment.id] } : appointment)))
          }
          if (deleted !== undefined) {
            setAvailability(availability.filter(appointment => appointment.id !== deleted))
          }

    }

    return (
            <Scheduler
                data={availability}
                firstDayOfWeek={1}
            >
                <ViewState
                    defaultCurrentDate={currentDate}
                />
                <WeekView
                    startDayHour={6}
                    cellDuration={120}
                    endDayHour={24}
                />
                <Appointments />
                <EditingState onCommitChanges={commitChanges}/>
                <IntegratedEditing />
                <AppointmentTooltip 
                    showOpenButton
                    showCloseButton
                    showDeleteButton
                />
                <DragDropProvider />
            </Scheduler>
    )
}

export default Availability
