import './App.css';
import Home from './components/Home.js'
import NavBar from './components/NavBar.js'
import SignIn from './components/SignIn.js'
import Schedule from './components/Schedule.js'
import NewEmployeeForm from './components/NewEmployeeForm.js'
import EmployeesView from './components/EmployeesView.js'
import Employee from './components/Employee.js'
import theme from './components/theme.js'
import {  ThemeProvider } from '@material-ui/core/styles'
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom'
import { useState } from 'react'

function App() {
  const [employee, setEmployee] = useState(['Bobby', 'Vance'])
  const [employees, setEmployees ] = useState([])
  const isUserAuthenticated = false
  const [count, setCount] = useState(10)

  const addEmployee = () => {
    const empId = employees.length
    const newEmployee = <Employee 
      empId={empId}
      firstName={employee[0]}
      lastName={employee[1]}
    />
    setEmployees([...employees, newEmployee])
  }

  const removeEmployee = () => {
    const temp = [...employees]
    temp.splice(0,1)
    setEmployees(temp)
  }

  const homeButton = () => {
    setCount(count+1)
  }

  return (
    <Router>
      <ThemeProvider theme={ theme }>
        <div className='App'>
          <header className='NavBar'>
            <NavBar />
          </header>

          <Route
            exact
            path="/"
            render={() => {
                return (
                  isUserAuthenticated ?
                  <Redirect to="/home" /> :
                  <Redirect to="/sign-in" /> 
                )
            }}
          />
          <Route path='/sign-in' component={ SignIn } />
          {/* <Route path='/home' component={ Home } /> */}
          <Route path='/home'>
            <Home count={count} click={homeButton}/>
          </Route>
          <Route path='/schedule' component={ Schedule } />
          <Route path='/employees'>
            <EmployeesView employees={employees} addEmployee={addEmployee} employee={employee}/>
          </Route>  

        </div>
      </ThemeProvider>
    </Router>
  );
}

export default App;
