import { Card, Typography, TextField, Button, MenuItem } from "@material-ui/core"
import { useState } from 'react'
import useStyles from './styles'
import { useDispatch } from 'react-redux'
import { addEmployee } from '../../../actions/employees'
import {formatPhoneNumber} from '../../../formatAndValidations/formatPhoneNumber'
import {isEmailValid} from '../../../formatAndValidations/validateEmail'
import { HOTLINE, COLDLINE, MAINTENANCE, FLOATER, BACKUPCASH, CASHIER, MEATPREP } from '../../../constants/positions'


const positions = [ MAINTENANCE, HOTLINE, COLDLINE, FLOATER, BACKUPCASH, CASHIER, MEATPREP]

const EmployeeForm = () => {
    const classes = useStyles()
    const dispatch = useDispatch()

    const [emailError, setEmailError] = useState(false)
    const [emailHelperText, setEmailHelperText] = useState('')
    const [firstNameError, setFirstNameError] = useState(false)
    const [firstNameHelperText, setFirstNameHelperText] = useState('')
    const [lastNameError, setLastNameError] = useState(false)
    const [lastNameHelperText, setLastNameHelperText] = useState('')

    const [employee, setEmployee] = useState({
        firstName: '',
        middleInitial: '',
        lastName: '',
        phoneNumber: '',
        email: '',
        positions: MAINTENANCE,
        availability: [
            { 
                id: 1,
                startDate: '2021-03-15T07:30',
                endDate: '2021-03-15T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 2,
                startDate: '2021-03-16T07:30',
                endDate: '2021-03-16T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 3,
                startDate: '2021-03-17T07:30',
                endDate: '2021-03-17T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 4,
                startDate: '2021-03-18T07:30',
                endDate: '2021-03-18T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 5,
                startDate: '2021-03-19T07:30',
                endDate: '2021-03-19T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 6,
                startDate: '2021-03-20T07:30',
                endDate: '2021-03-20T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
            { 
                id: 0,
                startDate: '2021-03-21T07:30',
                endDate: '2021-03-21T22:30',
                rRule: 'FREQ=WEEKLY;WKST=SU'
            },
        ],
    }) 

    const handleClick = (e) => {
        const fieldName = e.target.name
        switch (fieldName){
            case 'email': {
                setEmailError(false)
                setEmailHelperText('')
                break
            }
            case 'firstName':{
                setFirstNameError(false)
                setFirstNameHelperText('')
                break
            }
            case 'lastName': {
                setLastNameError(false)
                setLastNameHelperText('')
                break
            }
        }
    }

    const handleFirstName = (e) => {
        employee.firstName == '' ? 
            setFirstNameError(true) && setFirstNameHelperText('Gotta have a first name!') :
            setFirstNameError(false) && setFirstNameHelperText('')
    }

    const handleSubmit = (e) => {
        e.preventDefault()

        if(isEmailValid(e.email) && e.firstName.length()>0 && e.lastName.length()>0 && e.phoneNumber.length()>9){
            dispatch(addEmployee(employee))
            clear()
        }
        else{
            setEmailError(true)
            setEmailHelperText('Email not valid!')
        }
    }

    const clear = () => {
        setEmployee({firstName: '', middleInitial: '', lastName: '', phoneNumber: '', email: '', positions: MAINTENANCE})
    }

    return (
        <Card className={classes.card} >
            <form className={`${classes.root} ${classes.form}`} autoComplete="off" noValidate onSubmit={handleSubmit} >
                {/* <Typography variant="h6">{currentId ? 'Editing' : 'Creating'} a Memory</Typography> */}
                <TextField name="firstName" required error={firstNameError} variant="outlined" label="First Name" fullWidth value={employee.firstName} helperText={firstNameHelperText} onChange={ (e) => setEmployee({ ...employee, firstName: e.target.value.trim() })} onBlur={ handleFirstName } />
                <TextField name="middleInitial" variant="outlined" label="Middle Initial" fullWidth value={employee.middleInitial} onChange={ (e) => setEmployee({ ...employee, middleInitial: e.target.value.toUpperCase() })} inputProps={{ maxLength:1 }}/>
                <TextField name="lastName" required error={lastNameError} variant="outlined" label="Last Name" fullWidth value={employee.lastName} helperText={lastNameHelperText} onChange={ (e) => setEmployee({ ...employee, lastName: e.target.value.trim() })} onClick={ (e) => handleClick(e) }/>
                <TextField name="phoneNumber" required variant="outlined" label="Phone Number" fullWidth value={employee.phoneNumber} onChange={ (e) => setEmployee({...employee, phoneNumber: formatPhoneNumber(e.target.value) })} />
                <TextField name="email" required error={emailError} variant="outlined" label="Email" fullWidth value={employee.email} helperText={emailHelperText} onChange={ (e) => setEmployee({...employee, email: e.target.value})} onClick={ (e) => handleClick(e) } />
                <TextField select name="position" required variant="outlined" label="Position" fullWidth value={employee.positions} onChange={ (e) => setEmployee({...employee, positions: e.target.value })} >
                    {positions.map((option) => (
                        <MenuItem key={option} value={option}>
                            {option}
                        </MenuItem>
                    ))}
                </TextField>
                <Button className={classes.buttonSubmit} variant="contained" color="primary" size="large"  type='submit' fullWidth>Submit</Button>
                {/* <Button variant="contained" color="secondary" size="small" onClick={clear} fullWidth>Clear</Button> */}
            </form>
        </Card>
    )
}

export default EmployeeForm
