import './App.css';
import Home from './components/Home.js'
import NavBar from './components/NavBar.js'
import SignIn from './components/SignIn.js'
import Schedule from './components/Schedule.js'
import CssBaseline from '@material-ui/core/CssBaseline'
import EmployeesView from './components/EmployeesView.js'
import Employee from './components/Employee.js'
import theme from './components/theme.js'
import {  ThemeProvider } from '@material-ui/core/styles'
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom'
import { useState } from 'react'

function App() {
  const [employees, setEmployees ] = useState([
    {
      firstName: 'Tyler',
      lastName: 'Vance',
      email: 'tylerisinthewater@gmail.com',
      position: 'Hot Line',
      phoneNumber: '423-605-1662',
    },
  ])

  const isUserAuthenticated = false
  const [count, setCount] = useState(0)

  const addEmployee = ({employee}) => {
    const newEmployee = {
      firstName: employee.firstName,
      lastName: employee.lastName,
      email: employee.email,
      phoneNumber: employee.phoneNumber,
      position: employee.position,
    }
    setEmployees([...employees, newEmployee])
    setCount(count+1)
  }

  const removeEmployee = () => {
    const temp = [...employees]
    temp.splice(0,1)
    setEmployees(temp)
  }

  const homeButton = () => {
    setCount(count+1)
  }

  return (
    <Router>
      <ThemeProvider theme={ theme }>
        <CssBaseline />
          <NavBar />

          <Route
            exact
            path="/"
            render={() => {
                return (
                  isUserAuthenticated ?
                  <Redirect to="/home" /> :
                  <Redirect to="/sign-in" /> 
                )
            }}
          />
          <Route path='/sign-in' component={ SignIn } />
          {/* <Route path='/home' component={ Home } /> */}
          <Route path='/home'>
            <Home count={count} click={homeButton}/>
          </Route>
          <Route path='/schedule' component={ Schedule } />
          <Route path='/employees'>
            <EmployeesView employees={employees} addEmployee={addEmployee} />
          </Route>  

      </ThemeProvider>
    </Router>
  );
}

export default App;
