import { useSelector } from 'react-redux'
import { useState } from 'react'
import { Menu, MenuItem, IconButton, Fade } from '@material-ui/core'
import MoreVertIcon from '@material-ui/icons/MoreVert'


const DropDownMenu = ({setSelectedEmployee}) => {
    const employees = useSelector((state) => state.employees)
    const [option, setOption] = useState(employees[0])
    const ITEM_HEIGHT = 48
    const [anchorEl, setAnchorEl] = useState(null)
    const open = Boolean(anchorEl)

    const handleClick = (event) => {
        setAnchorEl(event.currentTarget)
    }

    const handleClose = (event) => {
        const name = event.target.innerText
        if(name) findByName(name)
        // console.log(name)
        setAnchorEl(null);
    }

    const findByName = (name) => {
        const employee = employees.find((emp) => {
            emp.firstName == name.split(' ')[0] && 
            emp.lastName == name.split(' ')[1]
        })
        console.log(employee)
    }


    return (
        <div>
            <IconButton
                aria-label="more"
                aria-controls="long-menu"
                aria-haspopup="true"
                onClick={handleClick}
            >
                <MoreVertIcon />
            </IconButton>
            <Menu
                id="long-menu"
                anchorEl={anchorEl}
                keepMounted
                value={option}
                open={open}
                onClose={handleClose}

                PaperProps={{
                    style: {
                        maxHeight: ITEM_HEIGHT * 4.5,
                        width: '40ch',
                    },
                }}
            >
            {employees.map((employee) => (
                <MenuItem key={employee} onClick={handleClose} >
                    {employee.firstName} {employee.lastName}
                </MenuItem>
            ))}
            </Menu>
        </div>
    )
}

export default DropDownMenu
