import { useState } from 'react';
import { AppBar, CssBaseline, Drawer, Hidden, IconButton, Toolbar, Typography, useTheme } from '@material-ui/core'
import useStyles from './styles'
import DrawerTabs from './DrawerTabs'
import SignIn from '../signInPage/SignInPage'
import HomePage from '../homePage/HomePage'

function a11yProps(index) {
    return {
      id: `vertical-tab-${index}`,
      'aria-controls': `vertical-tabpanel-${index}`,
    };
}

const Navigation = (props) => {
    const { window } = props;
    const theme = useTheme();
    const classes = useStyles();
    const [mobileOpen, setMobileOpen] = useState(false);
    const pages = props.pages
    const [page, setPage] = useState(null);

    const handleDrawerToggle = () => {
        setMobileOpen(!mobileOpen);
    };

    const handlePageSelect = (event, selectedPage) => {
        setPage(selectedPage);
    };


    const container = window !== undefined ? () => window().document.body : undefined;

    return (
        <div className={classes.root}>
            <CssBaseline />
            <AppBar position="fixed" className={classes.appBar}>
                <Toolbar>
                    <IconButton
                        color="inherit"
                        aria-label="open drawer"
                        edge="start"
                        onClick={handleDrawerToggle}
                        className={classes.menuButton}
                    >
                        <MenuIcon />
                    </IconButton>
                    <Typography variant="h6" noWrap>

                    </Typography>
                </Toolbar>
            </AppBar>
            <nav className={classes.drawer} aria-label="pages">
                {/* The implementation can be swapped from css to js to avoid SEO duplication of links. */}
                <Hidden smUp implementation="js">
                    <Drawer
                        container={container}
                        variant="temporary"
                        anchor={theme.direction === 'rtl' ? 'right' : 'left'}
                        open={mobileOpen}
                        onClose={handleDrawerToggle}
                        classes={{
                        paper: classes.drawerPaper,
                        }}
                        ModalProps={{
                        keepMounted: true, // Better open performance on mobile.
                        }}
                    >
                        <Tabs
                            orientation="vertical"
                            variant="scrollable"
                            value={page}
                            onChange={handlePageSelect}
                            aria-label="Pages"
                            // className={classes.drawerTabs}
                            // color={theme.palette.primary.main}
                        >
                            <Tab label="Home" {...a11yProps(0)} />
                            <Tab label="Schedule" {...a11yProps(1)} />
                            <Tab label="Employees" {...a11yProps(2)} />
                        </Tabs>
                    </Drawer>
                </Hidden>
                <Hidden xsDown implementation="js">
                    <Drawer
                        classes={{paper: classes.drawerPaper,}}
                        variant="permanent"
                        open
                    >
                        <DrawerTabs />
                    </Drawer>
                </Hidden>
            </nav>
            <main className={classes.content}>
                {page}
            </main>
        </div>
    )
}

export default Navigation
